import React, { useState, useEffect } from 'react';
import { fetchResultByTermAndResource } from '../api/requests';

const SearchBar = (props) => {
    const {setSearchResults} = props
    const [searchTerm, setSearchTerm] = useState("");
    const [searchValue, setSearchValue] = useState("");
    const onSubmitHandler = async ()=> {

            try {
             const results = await  fetchResultByTermAndResource({searchTerm,searchValue}, "Object")  ;
             const {records} = results;
             setSearchResults(records);
            }catch (error) {
                throw error
            }
    }
    useEffect(()=> {

    })

    return <><form id = "searchForm" onSubmit= {(event) =>{
        event.preventDefault();
        onSubmitHandler()}}>
             <div className="field">
            <label>Search Term</label>
            <input
            type = "text"
            label = "Title"
            placeholder="Search Term"
            value = {searchTerm}
            required
            onChange = { (event) => {
                setSearchTerm(event.target.value);}}
            />

        </div>

        <div className="field">
            <label>Search Value</label>
            <input
            type = "text"
            label = "Value"
            value = {searchValue}
            placeholder="Search Value"
            required
            onChange = {(event) => {setSearchValue(event.target.value)}}
            />

        </div>
        <button className="submit-form" type = "submit" value = "Submit">Submit</button>

            </form></>
}

export default SearchBar
